(
SELECT
  dep3.*,
  
  1-CASE WHEN hetd > 0 THEN SUM( hetd_pourc ) OVER ( PARTITION BY periode_paiement_id, intervenant_id, etat, structure_id) ELSE 0 END pourc_ecart
  
  
FROM (

SELECT 
  periode_paiement_id,
  structure_id,
  intervenant_type_id,
  intervenant_id, 
  annee_id, 
  centre_cout_id, 
  domaine_fonctionnel_id,
  etat,
