    (
        SELECT s2.ID 
        FROM SERVICE s2
        INNER JOIN ELEMENT_PEDAGOGIQUE ep2      ON s2.ELEMENT_PEDAGOGIQUE_ID  = ep2.ID  AND 1 = OSE_DIVERS.COMPRISE_ENTRE(ep2.HISTO_CREATION, ep2.HISTO_DESTRUCTION)
