        WHERE 
            s2.INTERVENANT_ID = v.intervenant_id
            AND 1 = OSE_DIVERS.COMPRISE_ENTRE(s2.HISTO_CREATION, s2.HISTO_DESTRUCTION)
            AND VH2.TYPE_VOLUME_HORAIRE_ID = V.TYPE_VOLUME_HORAIRE_ID
