  CREATE OR REPLACE TRIGGER "OSE"."T_AGR_AGREMENT" 
AFTER INSERT 
OR UPDATE OF 
    type_agrement_id,
		intervenant_id,
		histo_creation,
		histo_destruction,
		structure_id
OR DELETE ON AGREMENT
FOR EACH ROW
BEGIN
