  CREATE OR REPLACE PACKAGE BODY "OSE"."OSE_EVENT" AS
  TYPE t_dep IS TABLE OF VARCHAR2(80) INDEX BY PLS_INTEGER;
  TYPE t_deps IS TABLE OF t_dep INDEX BY VARCHAR2(80);

