  END;


  FUNCTION GET_DEPS RETURN t_deps IS 
    i PLS_INTEGER DEFAULT 1;
    deps t_deps;
  BEGIN
    FOR d IN (
      SELECT
        CONNECT_BY_ROOT(p1) pref, p2 pdep
      FROM
        package_deps pd 
      WHERE
        p2 IS NOT NULL
      CONNECT BY
        PRIOR p2 = p1
      ORDER BY
        LEVEL DESC
    ) LOOP
      deps(d.pref)(i) := d.pdep;
      i := i + 1;
    END LOOP;

    RETURN deps;
  END;



  FUNCTION GET_SUCS RETURN t_deps IS 
    i PLS_INTEGER DEFAULT 1;
    deps t_deps;
  BEGIN
    FOR d IN (
      SELECT
        CONNECT_BY_ROOT(p2) pref, p1 pdep
      FROM
        package_deps pd 
      WHERE
        p2 IS NOT NULL
      CONNECT BY
        p2 = PRIOR p1
      ORDER BY
        LEVEL
    ) LOOP
      IF d.pref IS NOT NULL THEN
        deps(d.pref)(i) := d.pdep;
        i := i + 1;
      END IF;
    END LOOP;
  
    RETURN deps;
  END;
  
  
  
  FUNCTION GET_ORDERED RETURN t_dep IS 
    i PLS_INTEGER DEFAULT 1;
    deps t_dep;
  BEGIN
    FOR d IN (
      WITH t AS (
        SELECT
          level n, CONNECT_BY_ROOT(p1) pdep
        FROM
          package_deps pd 
        CONNECT BY NOCYCLE
          p1 = PRIOR p2
      )
      SELECT
        MAX(n) o, pdep
      FROM
        t
      GROUP BY
        pdep
      ORDER BY
        o, pdep
    ) LOOP
      deps(i) := d.pdep;
      i := i + 1;
    END LOOP;
  
    RETURN deps;
  END;



