FUNCTION ldap_connect RETURN NUMBER IS
  ldap_host   VARCHAR2(256 char) := 'ldap.unicaen.fr';
  ldap_port   VARCHAR2(3 char)   := '389';
  ldap_user   VARCHAR2(256 char) := 'uid=authinfo,ou=system,dc=unicaen,dc=fr' ;
