  l_retval  PLS_INTEGER ;

  resultat  VARCHAR2(1024 char) := NULL ;

BEGIN
  -- Ouverture de connexion
  BEGIN
  ldap_sess := DBMS_LDAP.init(hostname => ldap_host,
                              portnum  => ldap_port) ;
  EXCEPTION
    WHEN DBMS_LDAP.INIT_FAILED THEN
      RETURN 1 ;
  END;


  -- Authentification
  BEGIN
