  FUNCTION calcFnc( fncName VARCHAR2, c VARCHAR2 ) RETURN FLOAT IS
    val FLOAT;
    cellRes FLOAT;
  BEGIN
    IF feuille.exists(\'__\' || fncName || \'__\' || c || \'__\') THEN
      IF feuille(\'__\' || fncName || \'__\' || c || \'__\').cells.exists(1) THEN
        RETURN feuille(\'__\' || fncName || \'__\' || c || \'__\').cells(1).valeur;
      END IF;
