    -- AD=SI(ET(AA$54=0;HC_autorisees="Oui");AA22-AC22;0)
    WHEN c = \'AD\' AND v >= 1 THEN
      IF cell(\'AA54\') = 0 AND NOT i.depassement_service_du_sans_hc THEN
        RETURN cell(\'AA\', l) - cell(\'AC\', l);
      ELSE
        RETURN 0;
