    -- AF=SI($H22="";0;SI(ET($D22="Rfrentiel");$J22;0))
    WHEN c = \'AF\' AND v >= 1 THEN
      IF vh.volume_horaire_ref_id IS NOT NULL THEN
        RETURN cell(\'J\', l);
