    -- AG=SI(AF$52>0;AF22/AF$52;0)
    WHEN c = \'AG\' AND v >= 1 THEN
      IF cell(\'AF52\') > 0 THEN
        RETURN cell(\'AF\', l) / cell(\'AF52\');
      ELSE
        RETURN 0;
