    -- AT=($O22+$T22+$AD22+$AN22)*SI(E22="";0;E22)
    WHEN c = \'AT\' AND v >= 1 THEN
      RETURN (cell(\'O\', l) + cell(\'T\', l) + cell(\'AD\', l) + cell(\'AN\', l)) * vh.taux_fi;



