  -- Cre une dfinition de tableau
  FUNCTION TC( tableau NUMERIC, version NUMERIC, options VARCHAR2 DEFAULT NULL) RETURN t_tableau_config IS
    tcRes t_tableau_config;
  BEGIN
    tcRes.tableau := tableau;
    tcRes.version := version;
