      -- saut de ligne complet
      chr(13) || chr(10) ||
      -- concatnation des lments non null spars par \', \'
      trim(trim(\',\' FROM REPLACE(\', \' || NVL(localite,\'#\') || \', \' || NVL(code_postal,\'#\') || \', \' || NVL(ville,\'#\') || \', \' || NVL(pays_libelle,\'#\'), \', #\', \'\')));
