  PROCEDURE LOAD_INTERVENANT_FROM_BDD IS
    TYPE t_formule_intervenant IS RECORD (
      intervenant_id                  NUMERIC,
      annee_id                        NUMERIC,
      type_intervenant_code           VARCHAR2(1),
      structure_code                  VARCHAR2(50),
