      intervenant.param_2                        := formule_intervenant.param_2;
      intervenant.param_3                        := formule_intervenant.param_3;
      intervenant.param_4                        := formule_intervenant.param_4;
      intervenant.param_5                        := formule_intervenant.param_5;

