            'definition' => 'CREATE OR REPLACE PACKAGE "OSE_TEST" AS

  DEBUG_ENABLED BOOLEAN DEFAULT FALSE;

  -- SET SERVEROUTPUT ON

  PROCEDURE SHOW_SUCCES;

