          si.peut_saisir_referentiel,
          SUM( CASE WHEN tvhs.code = \'\'PREVU\'\'   THEN NVL(vh .heures,0) ELSE 0 END ) heures_service_prev,
          SUM( CASE WHEN tvhs.code = \'\'PREVU\'\'   THEN NVL(vhr.heures,0) ELSE 0 END ) heures_referentiel_prev,
