      ID,
      ANNEE_ID,
      INTERVENANT_ID,
      STRUCTURE_ID,
      TYPE_VOLUME_HORAIRE_ID,
      SERVICE_REFERENTIEL_ID,
      VALIDATION_ID,
      VOLUME_HORAIRE_REF_ID,
      AUTO_VALIDATION,
      TO_DELETE

    ) VALUES (

      TBL_VALIDATION_REFERENT_ID_SEQ.NEXTVAL,
      v.ANNEE_ID,
      v.INTERVENANT_ID,
