      v.TYPE_VOLUME_HORAIRE_ID,
      v.SERVICE_REFERENTIEL_ID,
      v.VALIDATION_ID,
      v.VOLUME_HORAIRE_REF_ID,
      v.AUTO_VALIDATION,
      0

    );

    DELETE TBL_VALIDATION_REFERENTIEL WHERE to_delete = 1 AND \' || conds || \';

    END;\';

